package ContinueCommand;

public class ContinueCommand {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1; i<=10;i++)
		{
			if(i==3)
			{
				continue;
			}
		
		System.out.println(i);
		
		}	
	}

}
/* in continue command once if value comes true it will skip that block
 * and will continue the chain to finish top command. 
 */