package StringMethods1;

public class ConvertingCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String s="WELCOME";
		
		System.out.println(s.toLowerCase()); // coverts to lower case but L & C must be capital
		
		
		s="welcome";
		 
		System.out.println(s.toUpperCase()); // used for upper case U and C must be capital 
		
		
	}

}
